import React, { useState } from 'react';
import api from './api';
export default function Login({ onLogin }) {
  const [email,setEmail]=useState(''), [password,setPassword]=useState(''), [err,setErr]=useState(null), [loading, setLoading] = useState(false);
  const submit = async (e) => {
    e.preventDefault(); 
    setErr(null);
    setLoading(true);
    try {
      const res = await api.login({ email: email.toLowerCase().trim(), password });
      onLogin(res.data.user, res.data.token);
    } catch (e) { 
      setErr(e.response?.data?.error || 'Login failed'); 
    } finally {
      setLoading(false);
    }
  }
  return (
    <div style={{ background:'white', padding:'32px', borderRadius:'12px', boxShadow:'0 4px 20px rgba(0,0,0,0.08)' }}>
      <h3 style={{ margin:'0 0 24px 0' }}>üîê Sign In to Your Account</h3>
      <form onSubmit={submit}>
        <div style={{ marginBottom:'16px' }}>
          <label>Email Address</label>
          <input placeholder="your@email.com" value={email} onChange={e=>setEmail(e.target.value)} required style={{ width:'100%' }} />
        </div>
        <div style={{ marginBottom:'16px' }}>
          <label>Password</label>
          <input placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢" type="password" value={password} onChange={e=>setPassword(e.target.value)} required style={{ width:'100%' }} />
        </div>
        {err && <div style={{ color:'#e53e3e', background:'#fff5f5', padding:'12px', borderRadius:'6px', marginBottom:'16px', fontSize:'14px', border:'1px solid #fc8181' }}>‚ö†Ô∏è {err}</div>}
        <div style={{ marginTop:'24px' }}>
          <button type="submit" disabled={loading} style={{ width:'100%', padding:'12px', opacity: loading ? 0.7 : 1, cursor: loading ? 'not-allowed' : 'pointer' }}>{loading ? 'üîÑ Signing in...' : 'üîì Sign In'}</button>
        </div>
      </form>
    </div>
  )
}
