import React, { useState } from 'react';
import api from './api';

export default function BookingModal({ date, onClose, onBooked }) {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [service, setService] = useState('Counseling');
  const [timeSlot, setTimeSlot] = useState('09:00-10:00');
  const [error, setError] = useState(null);

  const submit = async (e) => {
    e.preventDefault();
    setError(null);
    try {
      await api.bookings.create({ service_type: service, date, time_slot: timeSlot });
      onBooked && onBooked();
    } catch (err) {
      setError(err.response?.data?.error || 'Booking failed');
    }
  }

  return (
    <div style={{ position:'fixed', left:0,right:0,top:0,bottom:0, background:'rgba(0,0,0,0.4)', display:'flex', alignItems:'center', justifyContent:'center' }}>
      <div style={{ background:'#fff', padding:20, width:420, borderRadius:8 }}>
        <h3>Book for {date}</h3>
        <form onSubmit={submit}>
          <div style={{ marginBottom:8 }}>
            <label>Service</label><br />
            <select value={service} onChange={e=>setService(e.target.value)} style={{ width:'100%' }}>
              <option>Counseling</option><option>Baptism</option><option>Wedding</option><option>Blessing</option><option>Facility Booking</option>
            </select>
          </div>
          <div style={{ marginBottom:8 }}>
            <label>Time Slot</label><br />
            <select value={timeSlot} onChange={e=>setTimeSlot(e.target.value)} style={{ width:'100%' }}>
              <option>09:00-10:00</option><option>10:00-11:00</option><option>11:00-12:00</option><option>13:00-14:00</option><option>14:00-15:00</option>
            </select>
          </div>
          {error && <div style={{ color:'red' }}>{error}</div>}
          <div style={{ display:'flex', justifyContent:'space-between', marginTop:12 }}>
            <button type="button" onClick={onClose}>Close</button>
            <button type="submit">Book</button>
          </div>
        </form>
      </div>
    </div>
  )
}
