import React, { useState } from 'react';
import api from './api';
export default function Login({ onLogin }) {
  const [email,setEmail]=useState(''), [password,setPassword]=useState(''), [err,setErr]=useState(null);
  const submit = async (e) => {
    e.preventDefault(); setErr(null);
    try {
      const res = await api.login({ email, password });
      onLogin(res.data.user, res.data.token);
    } catch (e) { setErr(e.response?.data?.error || 'Login failed'); }
  }
  return (
    <div style={{ maxWidth:420 }}>
      <h3>Login</h3>
      <form onSubmit={submit}>
        <div><input placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} required style={{ width:'100%' }} /></div>
        <div><input placeholder="Password" type="password" value={password} onChange={e=>setPassword(e.target.value)} required style={{ width:'100%', marginTop:8 }} /></div>
        {err && <div style={{ color:'red' }}>{err}</div>}
        <div style={{ marginTop:8 }}><button type="submit">Login</button></div>
      </form>
    </div>
  )
}
