import React, { useState } from 'react';
import api from './api';
export default function Register({ onRegister }) {
  const [name,setName]=useState(''), [email,setEmail]=useState(''), [pass,setPass]=useState(''), [err,setErr]=useState(null);
  const submit = async (e) => {
    e.preventDefault(); setErr(null);
    try {
      const res = await api.register({ name, email, password: pass });
      onRegister(res.data.user, res.data.token);
    } catch (e) { setErr(e.response?.data?.error || 'Register failed'); }
  }
  return (
    <div style={{ maxWidth:420 }}>
      <h3>Register</h3>
      <form onSubmit={submit}>
        <div><input placeholder="Full name" value={name} onChange={e=>setName(e.target.value)} required style={{ width:'100%' }} /></div>
        <div><input placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} required style={{ width:'100%', marginTop:8 }} /></div>
        <div><input placeholder="Password" type="password" value={pass} onChange={e=>setPass(e.target.value)} required style={{ width:'100%', marginTop:8 }} /></div>
        {err && <div style={{ color:'red' }}>{err}</div>}
        <div style={{ marginTop:8 }}><button type="submit">Register</button></div>
      </form>
    </div>
  )
}
