import React, { useEffect, useState } from 'react';
import api from './api';
export default function AdminDashboard({ addNotification }) {
  const [date, setDate] = useState('');
  const [maxSlots, setMaxSlots] = useState(5);
  const [users, setUsers] = useState([]);
  const [bookings, setBookings] = useState([]);

  const load = async () => {
    try {
      const u = await api.users.list(); setUsers(u.data);
      const b = await api.bookings.list(); setBookings(b.data);
    } catch(e){ console.error(e); }
  }

  useEffect(()=>{ load(); },[]);

  const saveConfig = async () => {
    try {
      await api.calendar.config({ date, max_slots: parseInt(maxSlots) });
      addNotification({ type:'info', text:`Set ${date} max slots ${maxSlots}`});
    } catch(e){ console.error(e); }
  }

  const delBooking = async (id) => {
    if (!confirm('Delete booking?')) return;
    try {
      await api.bookings.delete(id);
      addNotification({ type:'info', text:'Booking deleted' });
      load();
    } catch(e){ console.error(e); }
  }

  return (
    <div style={{ marginTop:20, padding:12, border:'1px solid #eee' }}>
      <h3>Admin Dashboard</h3>
      <div>
        <h4>Set max slots for a date</h4>
        <input type="date" value={date} onChange={e=>setDate(e.target.value)} /> <input type="number" value={maxSlots} onChange={e=>setMaxSlots(e.target.value)} style={{ width:80 }} /> <button onClick={saveConfig}>Save</button>
      </div>
      <div>
        <h4>Users</h4>
        <ul>{users.map(u => <li key={u.id}>{u.name} ({u.email}) - {u.role}</li>)}</ul>
      </div>
      <div>
        <h4>Bookings</h4>
        <table style={{ width:'100%', borderCollapse:'collapse' }}><thead><tr><th>Date</th><th>Time</th><th>Service</th><th>User</th><th></th></tr></thead>
          <tbody>{bookings.map(b => <tr key={b.id}><td>{b.date}</td><td>{b.time_slot}</td><td>{b.service_type}</td><td>{b.user_name}</td><td><button onClick={()=>delBooking(b.id)}>Delete</button></td></tr>)}</tbody></table>
      </div>
    </div>
  )
}
