import React, { useState, useEffect, useContext } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';
import api from './api';
import BookingModal from './BookingModal';
import { SocketContext } from './App';

export default function CalendarView({ addNotification }){
  const [events, setEvents] = useState([]);
  const [dateMap, setDateMap] = useState({});
  const [selectedDate, setSelectedDate] = useState(null);
  const socket = useContext(SocketContext);

  const fetchBookings = async () => {
    try {
      const res = await api.bookings.list();
      const ev = res.data.map(b => ({ id:b.id, title:`${b.service_type} — ${b.user_name}`, start:`${b.date}T${b.time_slot.split('-')[0]}` }));
      setEvents(ev);
    } catch(e){ console.error(e); }
  }

  const fetchCalendar = async () => {
    try {
      const res = await api.calendar.get();
      setDateMap(res.data || {});
    } catch(e){ console.error(e); }
  }

  useEffect(()=> {
    fetchBookings(); fetchCalendar();
    socket.on('new_booking', ()=>{ fetchBookings(); fetchCalendar(); });
    socket.on('booking_deleted', ()=>{ fetchBookings(); fetchCalendar(); });
    socket.on('calendar_config_updated', ()=> fetchCalendar());
    return ()=> { socket.off('new_booking'); socket.off('booking_deleted'); socket.off('calendar_config_updated'); }
  },[]);

  const handleDateClick = (arg) => setSelectedDate(arg.dateStr);

  const bgEvents = Object.entries(dateMap).map(([date, info]) => {
    const status = (info.booked >= info.max_slots) ? 'red' : 'green';
    return { id:`bg-${date}`, start: date, display:'background', allDay:true, backgroundColor: status==='red' ? 'rgba(255,0,0,0.3)' : 'rgba(0,200,0,0.18)' }
  });

  return (
    <div>
      <FullCalendar plugins={[dayGridPlugin, interactionPlugin]} initialView="dayGridMonth" height="auto" events={[...events,...bgEvents]} dateClick={handleDateClick} />
      <div style={{ marginTop:16 }}>
        <strong>Legend:</strong>
        <div><span style={{ background:'rgba(0,200,0,0.18)', padding:'6px 10px', marginRight:8 }}></span> Available</div>
        <div><span style={{ background:'rgba(255,0,0,0.3)', padding:'6px 10px', marginRight:8 }}></span> Fully booked</div>
      </div>
      {selectedDate && <BookingModal date={selectedDate} onClose={()=>setSelectedDate(null)} onBooked={()=>{ addNotification({ type:'info', text:`Booked on ${selectedDate}`}); setSelectedDate(null); fetchBookings(); fetchCalendar(); }} />}
    </div>
  )
}
